INSERT INTO `article` VALUES (224, '《凌云诺》手游主题曲《凌云赋》', 'http://www.javalm.cn/file/1.mp3', 'https://wsingbssdl.kugou.com/234632d0d5b1534bd8ab250deeaaf216.jpg', '欧美,动漫', '作为友谊时光强推的年度大型女性古风手游《凌云诺》的主题曲，歌曲中融入了戏腔、rap、古风元素，为听众和玩家带来不一样的古风音乐表现方式，将游戏中女性自强，报效国家，创盛世太平的积极精神传达给大家。', '凌云诺手游', 0, '正常', '蓝光', '2022-03-13 23:05:59');
